import React, { useState } from 'react';
import { gql, useLazyQuery } from '@apollo/client';
import '../styles/DeliveryTracking.css'; // Will create this CSS file next

const TRACK_DELIVERY = gql`
  query TrackDelivery($service_id: String!) {
    trackDelivery(service_id: $service_id) {
      service_id
      status
      estimated_delivery
      actual_delivery
      tracking_notes
    }
  }
`;

const DeliveryTrackingPage = () => {
  const [serviceId, setServiceId] = useState('');
  const [trackDelivery, { loading, data, error }] = useLazyQuery(TRACK_DELIVERY);
  const [searchedId, setSearchedId] = useState(null); // Keep track of the ID that was searched

  const handleTrack = (e) => {
    e.preventDefault();
    if (serviceId) {
      setSearchedId(serviceId);
      trackDelivery({ variables: { service_id: serviceId } });
    }
  };

  const renderEstimatedDelivery = (estimated_delivery_string) => {
    if (!estimated_delivery_string) {
      return "Not available";
    }
    const date = new Date(estimated_delivery_string);
    if (isNaN(date.getTime())) {
      return "Invalid date format";
    }
    return date.toLocaleDateString();
  };

  return (
    <div className="delivery-tracking-container">
      <h2>Track Your Delivery</h2>
      <form onSubmit={handleTrack} className="tracking-form">
        <input
          type="text"
          placeholder="Enter Tracking ID"
          value={serviceId}
          onChange={(e) => setServiceId(e.target.value)}
          className="form-control"
          required
        />
        <button type="submit" className="btn btn-primary" disabled={loading}>
          {loading ? 'Tracking...' : 'Track Order'}
        </button>
      </form>

      {searchedId && loading && <p className="loading">Tracking delivery...</p>}
      {searchedId && error && <p className="error">Error: {error.message}</p>}
      {searchedId && data && !data.trackDelivery && <p className="no-tracking-found">No tracking information found for ID: {searchedId}</p>}
      
      {searchedId && data?.trackDelivery && (
        <div className="tracking-results">
          <h3>Tracking Details for ID: {data.trackDelivery.service_id}</h3>
          <p><b>Status:</b> <span className={`status ${data.trackDelivery.status.toLowerCase().replace(/ /g, '_')}`}>{data.trackDelivery.status}</span></p>
          <p><b>Estimated Delivery:</b> {renderEstimatedDelivery(data.trackDelivery.estimated_delivery)}</p>
          {data.trackDelivery.actual_delivery && (
            <p><b>Actual Delivery:</b> {new Date(data.trackDelivery.actual_delivery).toLocaleDateString()}</p>
          )}
          {data.trackDelivery.tracking_notes && (
            <p><b>Notes:</b> {data.trackDelivery.tracking_notes}</p>
          )}
        </div>
      )}
    </div>
  );
};

export default DeliveryTrackingPage;
