const express = require('express');
const { ApolloServer } = require('@apollo/server');
const { expressMiddleware } = require('@apollo/server/express4');
const { buildSubgraphSchema } = require('@apollo/subgraph');
const { gql } = require('graphql-tag');
const mysql = require('mysql2/promise');
const cors = require('cors');
const fetch = require('node-fetch');

// HARDCODED URLs for debugging - TEMPORARY FIX
const PRODUCT_SERVICE_URL = 'http://localhost:4002';
const INVENTORY_SERVICE_URL = 'http://localhost:4004';  
const DELIVERY_SERVICE_URL = 'http://localhost:4005';

console.log('üîß HARDCODED Service URLs:');
console.log('  PRODUCT_SERVICE_URL:', PRODUCT_SERVICE_URL);
console.log('  INVENTORY_SERVICE_URL:', INVENTORY_SERVICE_URL);
console.log('  DELIVERY_SERVICE_URL:', DELIVERY_SERVICE_URL);

// Database connection
let db;
const connectDB = async (maxRetries = 10, retryInterval = 5000) => {
  for (let i = 0; i < maxRetries; i++) {
    try {
      console.log(`Attempting to connect to database... (${i + 1}/${maxRetries})`);
      db = await mysql.createConnection({
        host: 'localhost',
        user: 'root',
        password: '',
        database: 'ecommerce_order',
        connectTimeout: 60000,
      });
      
      await db.ping();
      console.log('‚úÖ Database connected successfully!');
      return;
    } catch (error) {
      console.log(`‚ùå Database connection failed (${i + 1}/${maxRetries}):`, error.message);
      if (i === maxRetries - 1) {
        throw error;
      }
      console.log(`‚è≥ Retrying in ${retryInterval / 1000} seconds...`);
      await new Promise(resolve => setTimeout(resolve, retryInterval));
    }
  }
};

// Simple service call function
const callService = async (url, query, variables = {}) => {
  try {
    console.log('üîó Calling service URL:', url);
    console.log('üìù Query:', query.substring(0, 100) + '...');
    console.log('üìä Variables:', variables);
    
    const response = await fetch(url, {
      method: 'POST',
      headers: { 
        'Content-Type': 'application/json',
        'Accept': 'application/json'
      },
      body: JSON.stringify({ query, variables })
    });
    
    if (!response.ok) {
      const errorText = await response.text();
      throw new Error(`HTTP ${response.status}: ${errorText}`);
    }
    
    const result = await response.json();
    
    if (result.errors) {
      throw new Error(`GraphQL: ${result.errors[0].message}`);
    }
    
    console.log('‚úÖ Service call successful');
    return result.data;
  } catch (error) {
    console.error('‚ùå Service call failed:', error.message);
    throw error;
  }
};

// REST API call function for delivery service
const callDeliveryRestAPI = async (endpoint, data) => {
  try {
    const url = `${DELIVERY_SERVICE_URL}/api/${endpoint}`;
    console.log('üöö Calling delivery API:', url);
    
    const response = await fetch(url, {
      method: 'POST',
      headers: { 
        'Content-Type': 'application/json',
        'Accept': 'application/json'
      },
      body: JSON.stringify(data)
    });
    
    if (!response.ok) {
      const errorText = await response.text();
      throw new Error(`HTTP ${response.status}: ${errorText}`);
    }
    
    const result = await response.json();
    console.log('‚úÖ Delivery API call successful');
    return result;
  } catch (error) {
    console.error('‚ùå Delivery API call failed:', error.message);
    throw error;
  }
};

// REST API call function for product service stock update
const callProductRestAPI = async (endpoint, data) => {
  try {
    const url = `${PRODUCT_SERVICE_URL}/api/${endpoint}`;
    console.log('üì¶ Calling product API:', url);
    
    const response = await fetch(url, {
      method: 'POST',
      headers: { 
        'Content-Type': 'application/json',
        'Accept': 'application/json'
      },
      body: JSON.stringify(data)
    });
    
    if (!response.ok) {
      const errorText = await response.text();
      throw new Error(`HTTP ${response.status}: ${errorText}`);
    }
    
    const result = await response.json();
    console.log('‚úÖ Product API call successful');
    return result;
  } catch (error) {
    console.error('‚ùå Product API call failed:', error.message);
    throw error;
  }
};

// GraphQL Schema - FIXED parameter types to match frontend
const typeDefs = gql`
  extend type Query {
    orders: [Order!]!
    order(id: ID!): Order
    myOrders: [Order!]!
    myCart: [CartItem!]!
  }

  extend type Mutation {
    addToCart(product_id: ID!, quantity: Int!): CartItem!
    removeFromCart(product_id: ID!): Boolean!
    updateCartItem(product_id: ID!, quantity: Int!): CartItem!
    clearCart: Boolean!
    createOrder(shipping_address: String!, payment_method: String!): Order!
    updateOrderStatus(id: ID!, status: String!): Order!
    cancelOrder(id: ID!): Order!
  }

  type Order @key(fields: "id") {
    id: ID!
    user_id: ID!
    total_amount: Float!
    status: String!
    shipping_address: String!
    payment_method: String
    created_at: String!
    updated_at: String!
    items: [OrderItem!]!
    user: User
    tracking: DeliveryTracking
  }

  type OrderItem {
    id: ID!
    order_id: ID!
    product_id: ID!
    quantity: Int!
    price: Float!
    product: Product
  }

  type CartItem {
    id: ID!
    user_id: ID!
    product_id: ID!
    quantity: Int!
    created_at: String!
    product: Product
  }

  type DeliveryTracking {
    service_id: String!
    status: String!
    estimated_delivery: String
    actual_delivery: String
    tracking_notes: String
  }

  extend type User @key(fields: "id") {
    id: ID! @external
    orders: [Order!]!
    cart: [CartItem!]!
  }

  extend type Product @key(fields: "id") {
    id: ID! @external
  }
`;

const resolvers = {
  Query: {
    orders: async (_, __, { user }) => {
      if (!user || user.role !== 'admin') throw new Error('Admin access required');
      const [rows] = await db.execute('SELECT * FROM orders ORDER BY created_at DESC');
      return rows;
    },
    
    order: async (_, { id }, { user }) => {
      if (!user) throw new Error('Authentication required');
      
      let query = 'SELECT * FROM orders WHERE id = ?';
      const params = [id];
      
      if (user.role !== 'admin') {
        query += ' AND user_id = ?';
        params.push(user.id);
      }
      
      const [rows] = await db.execute(query, params);
      return rows[0];
    },
    
    myOrders: async (_, __, { user }) => {
      if (!user) throw new Error('Authentication required');
      const [rows] = await db.execute(
        'SELECT * FROM orders WHERE user_id = ? ORDER BY created_at DESC',
        [user.id]
      );
      return rows;
    },
    
    myCart: async (_, __, { user }) => {
      if (!user) throw new Error('Authentication required');
      const [rows] = await db.execute(
        'SELECT * FROM cart WHERE user_id = ? ORDER BY created_at DESC',
        [user.id]
      );
      return rows;
    }
  },
  
  Mutation: {
    addToCart: async (_, { product_id, quantity }, { user }) => {
      if (!user) throw new Error('Authentication required');
      
      console.log(`üõí Adding to cart: product_id=${product_id} (${typeof product_id}), quantity=${quantity}, user=${user.id}`);
      
      // Convert product_id to integer for database operations
      const productIdInt = parseInt(product_id);
      if (isNaN(productIdInt)) {
        throw new Error('Invalid product ID');
      }
      
      // Build the full URL for product service
      const productServiceUrl = `${PRODUCT_SERVICE_URL}/graphql`;
      console.log('üîó Product service URL:', productServiceUrl);
      
      // Verify product exists and has sufficient stock
      const productData = await callService(
        productServiceUrl,
        `query GetProduct($id: ID!) { 
          product(id: $id) { 
            id 
            name
            price
            stock 
          } 
        }`,
        { id: product_id } // Use original string ID for GraphQL
      );
      
      if (!productData?.product) {
        throw new Error('Product not found');
      }
      
      if (productData.product.stock < quantity) {
        throw new Error(`Insufficient stock available. Only ${productData.product.stock} items left.`);
      }
      
      // Check current cart quantity for this product
      const [existingCart] = await db.execute(
        'SELECT quantity FROM cart WHERE user_id = ? AND product_id = ?',
        [user.id, productIdInt]
      );
      
      const currentCartQuantity = existingCart.length > 0 ? existingCart[0].quantity : 0;
      const totalRequestedQuantity = currentCartQuantity + quantity;
      
      if (totalRequestedQuantity > productData.product.stock) {
        throw new Error(`Cannot add ${quantity} items. You already have ${currentCartQuantity} in cart and only ${productData.product.stock} items available.`);
      }
      
      // Add to cart or update existing item
      await db.execute(
        `INSERT INTO cart (user_id, product_id, quantity) 
         VALUES (?, ?, ?) 
         ON DUPLICATE KEY UPDATE quantity = quantity + ?`,
        [user.id, productIdInt, quantity, quantity]
      );
      
      const [rows] = await db.execute(
        'SELECT * FROM cart WHERE user_id = ? AND product_id = ?',
        [user.id, productIdInt]
      );
      
      console.log('‚úÖ Successfully added to cart');
      return rows[0];
    },
    
    createOrder: async (_, { shipping_address, payment_method }, { user }) => {
      if (!user) throw new Error('Authentication required');
      if (user.role !== 'customer') throw new Error('Only customers can place orders');
      
      console.log(`üì¶ Creating order for user ${user.id}`);
      
      // Get cart items
      const [cartItems] = await db.execute(
        'SELECT * FROM cart WHERE user_id = ?',
        [user.id]
      );
      
      if (cartItems.length === 0) {
        throw new Error('Cart is empty');
      }
      
      console.log(`üìù Found ${cartItems.length} items in cart`);
      
      // Validate products and calculate total
      let total = 0;
      const orderItems = [];
      
      const productServiceUrl = `${PRODUCT_SERVICE_URL}/graphql`;
      
      for (const item of cartItems) {
        console.log(`üîç Validating product ${item.product_id}`);
        
        const productData = await callService(
          productServiceUrl,
          `query GetProduct($id: ID!) { 
            product(id: $id) { 
              id 
              name
              price 
              stock 
            } 
          }`,
          { id: String(item.product_id) } // Convert to string for GraphQL
        );
        
        if (!productData?.product) {
          throw new Error(`Product ${item.product_id} not found`);
        }
        
        if (productData.product.stock < item.quantity) {
          throw new Error(`Insufficient stock for product ${productData.product.name}. Available: ${productData.product.stock}, Requested: ${item.quantity}`);
        }
        
        const itemTotal = productData.product.price * item.quantity;
        total += itemTotal;
        
        orderItems.push({
          product_id: item.product_id,
          quantity: item.quantity,
          price: productData.product.price
        });
      }
      
      console.log(`üí∞ Order total: $${total}`);
      
      // Create order
      const [orderResult] = await db.execute(
        'INSERT INTO orders (user_id, total_amount, shipping_address, payment_method) VALUES (?, ?, ?, ?)',
        [user.id, total, shipping_address, payment_method]
      );
      
      const orderId = orderResult.insertId;
      console.log(`üìÑ Created order ${orderId}`);
      
      // Create order items
      for (const item of orderItems) {
        await db.execute(
          'INSERT INTO order_items (order_id, product_id, quantity, price) VALUES (?, ?, ?, ?)',
          [orderId, item.product_id, item.quantity, item.price]
        );
      }
      
      // Update product stock for each item using REST API
      console.log('üì¶ Updating product stock...');
      for (const item of orderItems) {
        try {
          await callProductRestAPI('update-stock', {
            product_id: item.product_id,
            stock_change: -item.quantity // Negative value to reduce stock
          });
          console.log(`‚úÖ Updated stock for product ${item.product_id}: -${item.quantity}`);
        } catch (error) {
          console.error(`‚ùå Failed to update stock for product ${item.product_id}:`, error.message);
          // Continue with other products, don't fail the entire order
        }
      }
      
      // Create delivery tracking automatically
      console.log('üöö Creating delivery tracking...');
      try {
        const trackingResult = await callDeliveryRestAPI('create-tracking', {
          order_id: orderId,
          estimated_delivery: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000).toISOString() // 7 days from now
        });
        
        if (trackingResult.success) {
          console.log(`‚úÖ Created delivery tracking: ${trackingResult.tracking.service_id}`);
        }
      } catch (error) {
        console.error('‚ùå Failed to create delivery tracking:', error.message);
        // Don't fail the order if tracking creation fails
      }
      
      // Clear cart
      await db.execute('DELETE FROM cart WHERE user_id = ?', [user.id]);
      console.log('üßπ Cart cleared');
      
      // Return created order
      const [rows] = await db.execute('SELECT * FROM orders WHERE id = ?', [orderId]);
      console.log('‚úÖ Order created successfully');
      return rows[0];
    },
    
    updateOrderStatus: async (_, { id, status }, { user }) => {
      if (!user || user.role !== 'admin') throw new Error('Admin access required');
      
      // Valid status values
      const validStatuses = ['pending', 'processing', 'shipped', 'delivered', 'cancelled'];
      if (!validStatuses.includes(status)) {
        throw new Error(`Invalid status. Must be one of: ${validStatuses.join(', ')}`);
      }
      
      await db.execute(
        'UPDATE orders SET status = ?, updated_at = CURRENT_TIMESTAMP WHERE id = ?',
        [status, id]
      );
      
      const [rows] = await db.execute('SELECT * FROM orders WHERE id = ?', [id]);
      
      if (rows.length === 0) {
        throw new Error('Order not found');
      }
      
      return rows[0];
    },
    
    removeFromCart: async (_, { product_id }, { user }) => {
      if (!user) throw new Error('Authentication required');
      
      const productIdInt = parseInt(product_id);
      if (isNaN(productIdInt)) {
        throw new Error('Invalid product ID');
      }
      
      await db.execute(
        'DELETE FROM cart WHERE user_id = ? AND product_id = ?',
        [user.id, productIdInt]
      );
      
      return true;
    },
    
    updateCartItem: async (_, { product_id, quantity }, { user }) => {
      if (!user) throw new Error('Authentication required');
      
      const productIdInt = parseInt(product_id);
      if (isNaN(productIdInt)) {
        throw new Error('Invalid product ID');
      }
      
      if (quantity <= 0) {
        // Remove item if quantity is 0 or negative
        await db.execute(
          'DELETE FROM cart WHERE user_id = ? AND product_id = ?',
          [user.id, productIdInt]
        );
        return null;
      }
      
      // Verify product exists and has sufficient stock
      const productServiceUrl = `${PRODUCT_SERVICE_URL}/graphql`;
      const productData = await callService(
        productServiceUrl,
        `query GetProduct($id: ID!) { 
          product(id: $id) { 
            id 
            stock 
          } 
        }`,
        { id: product_id }
      );
      
      if (!productData?.product) {
        throw new Error('Product not found');
      }
      
      if (productData.product.stock < quantity) {
        throw new Error(`Insufficient stock available. Only ${productData.product.stock} items in stock.`);
      }
      
      await db.execute(
        'UPDATE cart SET quantity = ? WHERE user_id = ? AND product_id = ?',
        [quantity, user.id, productIdInt]
      );
      
      const [rows] = await db.execute(
        'SELECT * FROM cart WHERE user_id = ? AND product_id = ?',
        [user.id, productIdInt]
      );
      
      return rows[0];
    },
    
    clearCart: async (_, __, { user }) => {
      if (!user) throw new Error('Authentication required');
      
      await db.execute('DELETE FROM cart WHERE user_id = ?', [user.id]);
      return true;
    },

    cancelOrder: async (_, { id }, { user }) => {
      if (!user) throw new Error('Authentication required');
      
      const orderIdInt = parseInt(id);
      if (isNaN(orderIdInt)) {
        throw new Error('Invalid order ID');
      }
      
      // Check if user owns the order (customers) or is admin
      let query = 'SELECT * FROM orders WHERE id = ?';
      const params = [orderIdInt];
      
      if (user.role !== 'admin') {
        query += ' AND user_id = ?';
        params.push(user.id);
      }
      
      const [orderRows] = await db.execute(query, params);
      
      if (orderRows.length === 0) {
        throw new Error('Order not found or access denied');
      }
      
      const order = orderRows[0];
      
      if (order.status === 'delivered') {
        throw new Error('Cannot cancel delivered order');
      }
      
      if (order.status === 'cancelled') {
        throw new Error('Order is already cancelled');
      }
      
      // Update order status to cancelled
      await db.execute(
        'UPDATE orders SET status = ?, updated_at = CURRENT_TIMESTAMP WHERE id = ?',
        ['cancelled', orderIdInt]
      );
      
      // Restore stock for cancelled orders
      const [orderItems] = await db.execute(
        'SELECT * FROM order_items WHERE order_id = ?',
        [orderIdInt]
      );
      
      for (const item of orderItems) {
        try {
          await callProductRestAPI('update-stock', {
            product_id: item.product_id,
            stock_change: item.quantity // Positive value to restore stock
          });
          console.log(`‚úÖ Restored stock for product ${item.product_id}: +${item.quantity}`);
        } catch (error) {
          console.error(`‚ùå Failed to restore stock for product ${item.product_id}:`, error.message);
        }
      }
      
      const [rows] = await db.execute('SELECT * FROM orders WHERE id = ?', [orderIdInt]);
      return rows[0];
    }
  },
  
  Order: {
    __resolveReference: async (order) => {
      const [rows] = await db.execute('SELECT * FROM orders WHERE id = ?', [order.id]);
      return rows[0];
    },
    
    items: async (order) => {
      const [rows] = await db.execute('SELECT * FROM order_items WHERE order_id = ?', [order.id]);
      return rows;
    },
    
    user: async (order) => {
      return { id: order.user_id };
    },

    tracking: async (order) => {
      try {
        // Call delivery service to get tracking info by looking for TRK + order.id pattern
        const trackingData = await callService(
          `${DELIVERY_SERVICE_URL}/graphql`,
          `query TrackByOrderId($order_id: String!) {
            trackByOrderId(order_id: $order_id) {
              service_id
              status
              estimated_delivery
              actual_delivery
              tracking_notes
            }
          }`,
          { order_id: String(order.id) }
        );
        
        return trackingData?.trackByOrderId || null;
      } catch (error) {
        console.error('Error fetching tracking info:', error.message);
        return null;
      }
    }
  },
  
  OrderItem: {
    product: async (orderItem) => {
      return { id: String(orderItem.product_id) };
    }
  },
  
  CartItem: {
    product: async (cartItem) => {
      return { id: String(cartItem.product_id) };
    }
  },
  
  User: {
    orders: async (user) => {
      const [rows] = await db.execute(
        'SELECT * FROM orders WHERE user_id = ? ORDER BY created_at DESC',
        [user.id]
      );
      return rows;
    },
    
    cart: async (user) => {
      const [rows] = await db.execute(
        'SELECT * FROM cart WHERE user_id = ? ORDER BY created_at DESC',
        [user.id]
      );
      return rows;
    }
  }
};

// Helper function to get user from headers
const getUserFromHeaders = (req) => {
  const userHeader = req.headers.user;
  return userHeader ? JSON.parse(userHeader) : null;
};

async function startServer() {
  await connectDB();
  
  const server = new ApolloServer({
    schema: buildSubgraphSchema([{ typeDefs, resolvers }])
  });

  await server.start();

  const app = express();
  app.use(cors());
  app.use(express.json());

  // Health check endpoint
  app.get('/health', async (req, res) => {
    try {
      await db.ping();
      res.json({ status: 'OK', service: 'order-service', timestamp: new Date().toISOString() });
    } catch (error) {
      console.error('Health check failed:', error);
      res.status(500).json({ status: 'ERROR', service: 'order-service', error: error.message });
    }
  });

  app.use(
    '/graphql',
    expressMiddleware(server, {
      context: async ({ req }) => {
        const user = getUserFromHeaders(req);
        return { user };
      },
    })
  );

  const PORT = 4003;
  app.listen(PORT, () => {
    console.log(`üöÄ Order service ready at http://localhost:${PORT}/graphql`);
    console.log(`üè• Health check available at http://localhost:${PORT}/health`);
  });
}

startServer().catch(console.error);
