import React, { useState, useEffect } from 'react';
import { ApolloClient, InMemoryCache, ApolloProvider, createHttpLink, gql, useQuery, useMutation } from '@apollo/client';
import { setContext } from '@apollo/client/link/context';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';
import AdminAllOrders from './components/AdminAllOrders';
import DeliveryTrackingPage from './components/DeliveryTrackingPage';
import Register from './components/Register';

// GraphQL Client Setup
const httpLink = createHttpLink({
  uri: 'http://localhost:4000/graphql', // API Gateway
});

const authLink = setContext((_, { headers }) => {
  const token = localStorage.getItem('token');
  return {
    headers: {
      ...headers,
      authorization: token ? `Bearer ${token}` : "",
    }
  };
});

const client = new ApolloClient({
  link: authLink.concat(httpLink),
  cache: new InMemoryCache()
});

// GraphQL Queries and Mutations
const GET_PRODUCTS = gql`
  query GetProducts($limit: Int, $offset: Int, $category_id: Int) {
    products(limit: $limit, offset: $offset, category_id: $category_id) {
      id
      name
      description
      price
      stock
      image_url
      category {
        name
      }
    }
  }
`;

const GET_CATEGORIES = gql`
  query GetCategories {
    categories {
      id
      name
      description
    }
  }
`;

const LOGIN_MUTATION = gql`
  mutation Login($email: String!, $password: String!) {
    login(email: $email, password: $password) {
      token
      user {
        id
        username
        email
        role
      }
    }
  }
`;

const REGISTER_MUTATION = gql`
  mutation Register($username: String!, $email: String!, $password: String!, $role: String) {
    register(username: $username, email: $email, password: $password, role: $role) {
      token
      user {
        id
        username
        email
        role
      }
    }
  }
`;

const GET_MY_CART = gql`
  query GetMyCart {
    myCart {
      id
      quantity
      product {
        id
        name
        price
        image_url
        stock
      }
    }
  }
`;

const ADD_TO_CART = gql`
  mutation AddToCart($product_id: ID!, $quantity: Int!) {
    addToCart(product_id: $product_id, quantity: $quantity) {
      id
      quantity
    }
  }
`;

const CREATE_ORDER = gql`
  mutation CreateOrder($shipping_address: String!, $payment_method: String!) {
    createOrder(shipping_address: $shipping_address, payment_method: $payment_method) {
      id
      total_amount
      status
      created_at
    }
  }
`;

const GET_MY_ORDERS = gql`
  query GetMyOrders {
    myOrders {
      id
      total_amount
      status
      shipping_address
      created_at
      items {
        id
        quantity
        price
        product {
          id
          name
          image_url
        }
      }
      tracking {
        service_id
        status
        estimated_delivery
        actual_delivery
        tracking_notes
      }
    }
  }
`;

// Fixed: Use correct field names for add product mutation
const ADD_PRODUCT = gql`
  mutation AddProduct($name: String!, $description: String, $price: Float!, $category_id: Int!, $stock: Int!, $image_url: String) {
    addProduct(name: $name, description: $description, price: $price, category_id: $category_id, stock: $stock, image_url: $image_url) {
      id
      name
      price
      stock
    }
  }
`;

// Fixed: Use 'id' instead of 'id_warehouse'
const GET_WAREHOUSES = gql`
  query GetWarehouses {
    warehouses {
      id
      name
      location
      manager_id
      created_at
      updated_at
    }
  }
`;

// Fixed: Use 'id' instead of 'id_warehouse'
const ADD_WAREHOUSE = gql`
  mutation AddWarehouse($name: String!, $location: String, $manager_id: Int) {
    addWarehouse(name: $name, location: $location, manager_id: $manager_id) {
      id
      name
      location
      manager_id
      created_at
    }
  }
`;

// Fixed: Use 'id' instead of 'id_inventory' and 'warehouse_id' instead of 'id_warehouse'
const GET_INVENTORY_PRODUCTS = gql`
  query GetInventoryProducts {
    inventoryProducts {
      id
      product_id
      warehouse_id
      quantity
      reserved
      restock_threshold
      last_restocked
      updated_at
    }
  }
`;

// Fixed: Use 'warehouse_id' instead of 'id_warehouse'
const ADD_INVENTORY_PRODUCT = gql`
  mutation AddInventoryProduct($product_id: ID!, $warehouse_id: ID!, $quantity: Int!, $reserved: Int, $restock_threshold: Int) {
    addInventoryProduct(product_id: $product_id, warehouse_id: $warehouse_id, quantity: $quantity, reserved: $reserved, restock_threshold: $restock_threshold) {
      id
      product_id
      warehouse_id
      quantity
      reserved
      restock_threshold
      last_restocked
    }
  }
`;

const GET_ALL_ORDERS = gql`
  query GetAllOrders {
    orders {
      id
      user_id
      total_amount
      status
      shipping_address
      created_at
      user { username email }
      tracking {
        service_id
        status
        estimated_delivery
        actual_delivery
        tracking_notes
      }
    }
  }
`;

const UPDATE_ORDER_STATUS = gql`
  mutation UpdateOrderStatus($id: ID!, $status: String!) {
    updateOrderStatus(id: $id, status: $status) {
      id
      status
    }
  }
`;

const UPDATE_DELIVERY_STATUS = gql`
  mutation UpdateDeliveryStatus($service_id: String!, $status: String!, $tracking_notes: String) {
    updateDeliveryStatus(service_id: $service_id, status: $status, tracking_notes: $tracking_notes) {
      service_id
      status
      tracking_notes
    }
  }
`;

const UPDATE_PROFILE = gql`
  mutation UpdateProfile($username: String, $password: String) {
    updateProfile(username: $username, password: $password) {
      id
      username
      email
      role
    }
  }
`;

// Components
const LoginForm = ({ onLogin, onToggleMode }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [login, { loading, error }] = useMutation(LOGIN_MUTATION);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const { data } = await login({ variables: { email, password } });
      localStorage.setItem('token', data.login.token);
      localStorage.setItem('user', JSON.stringify(data.login.user));
      onLogin(data.login.user);
    } catch (err) {
      console.error('Login error:', err);
    }
  };

  return (
    <div className="auth-form">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          placeholder="Email (try: admin@example.com)"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password (try: password)"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit" disabled={loading}>
          {loading ? 'Logging in...' : 'Login'}
        </button>
      </form>
      {error && <p className="error">Error: {error.message}</p>}
      <p>
        Don't have an account?{' '}
        <button type="button" onClick={onToggleMode} className="link-button">
          Register
        </button>
      </p>
      <div style={{ marginTop: '1rem', padding: '1rem', backgroundColor: '#f8f9fa', borderRadius: '6px', fontSize: '0.9rem' }}>
        <strong>Demo Credentials:</strong><br />
        Admin: admin@example.com / password<br />
        Customer: customer1@example.com / password
      </div>
    </div>
  );
};

