// Toast Notification Component
const Toast = ({ message, onClose }) => {
  useEffect(() => {
    const timer = setTimeout(onClose, 2200);
    return () => clearTimeout(timer);
  }, [onClose]);
  return <div className="toast">{message}</div>;
};

const ProductList = ({ user, setCurrentPage, showToast }) => {
  const [selectedCategory, setSelectedCategory] = useState(null);
  const { loading, error, data } = useQuery(GET_PRODUCTS, {
    variables: { limit: 20, offset: 0, category_id: selectedCategory }
  });
  const { data: categoriesData } = useQuery(GET_CATEGORIES);
  const [addToCart] = useMutation(ADD_TO_CART, {
    refetchQueries: [{ query: GET_MY_CART }]
  });

  const handleAddToCart = async (productId) => {
    if (!user) {
      alert('Please login to add items to cart');
      return;
    }
    try {
      await addToCart({ variables: { product_id: productId, quantity: 1 } });
      showToast('Product added to cart!');
      setCurrentPage('cart');
    } catch (err) {
      alert('Error adding to cart: ' + err.message);
    }
  };

  if (loading) return <div className="loading">Loading products...</div>;
  if (error) return <div className="error">Error: {error.message}</div>;

  return (
    <div className="product-section">
      <div className="category-filter">
        <button 
          onClick={() => setSelectedCategory(null)}
          className={selectedCategory === null ? 'active' : ''}
        >
          All Categories
        </button>
        {categoriesData?.categories.map(category => (
          <button
            key={category.id}
            onClick={() => setSelectedCategory(parseInt(category.id))} // Fixed: Parse to integer
            className={selectedCategory === parseInt(category.id) ? 'active' : ''} // Fixed: Parse to integer for comparison
          >
            {category.name}
          </button>
        ))}
      </div>
      
      <div className="products-grid">
        {data?.products.map(product => (
          <div key={product.id} className="product-card">
            <img src={product.image_url || '/placeholder.jpg'} alt={product.name} />
            <div className="product-info">
              <h3>{product.name}</h3>
              <p className="description">{product.description}</p>
              <div className="product-details">
                <span className="price">${product.price}</span>
                <span className="stock">Stock: {product.stock}</span>
                {product.category && <span className="category">{product.category.name}</span>}
              </div>
              {user?.role === 'customer' && (
                <button
                  onClick={() => handleAddToCart(product.id)}
                  disabled={product.stock === 0}
                  className="add-to-cart-btn"
                >
                  {product.stock === 0 ? 'Out of Stock' : 'Add to Cart'}
                </button>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

const Cart = ({ user }) => {
  const { loading, error, data } = useQuery(GET_MY_CART);
  const [createOrder] = useMutation(CREATE_ORDER, {
    refetchQueries: [{ query: GET_MY_CART }, { query: GET_MY_ORDERS }]
  });
  
  const [shippingAddress, setShippingAddress] = useState('');
  const [paymentMethod, setPaymentMethod] = useState('credit_card');
  const [showCheckout, setShowCheckout] = useState(false);

  if (!user) return <div>Please login to view cart</div>;
  if (loading) return <div className="loading">Loading cart...</div>;
  if (error) return <div className="error">Error: {error.message}</div>;

  const cartItems = data?.myCart || [];
  const total = cartItems.reduce((sum, item) => sum + (item.product.price * item.quantity), 0);

  const handleCheckout = async (e) => {
    e.preventDefault();
    try {
      await createOrder({
        variables: {
          shipping_address: shippingAddress,
          payment_method: paymentMethod
        }
      });
      alert('Order created successfully!');
      setShowCheckout(false);
      setShippingAddress('');
    } catch (err) {
      alert('Error creating order: ' + err.message);
    }
  };

  return (
    <div className="cart">
      <h2>Shopping Cart</h2>
      {cartItems.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <>
          <div className="cart-items">
            {cartItems.map(item => (
              <div key={item.id} className="cart-item">
                <img src={item.product.image_url || '/placeholder.jpg'} alt={item.product.name} />
                <div className="item-details">
                  <h4>{item.product.name}</h4>
                  <p>Price: ${item.product.price}</p>
                  <p>Quantity: {item.quantity}</p>
                  <p>Subtotal: ${(item.product.price * item.quantity).toFixed(2)}</p>
                </div>
              </div>
            ))}
          </div>
          <div className="cart-total">
            <h3>Total: ${total.toFixed(2)}</h3>
            <button onClick={() => setShowCheckout(!showCheckout)} className="checkout-btn">
              {showCheckout ? 'Cancel' : 'Proceed to Checkout'}
            </button>
          </div>
          
          {showCheckout && (
            <form onSubmit={handleCheckout} className="checkout-form">
              <h3>Checkout</h3>
              <textarea
                placeholder="Shipping Address"
                value={shippingAddress}
                onChange={(e) => setShippingAddress(e.target.value)}
                required
                rows="3"
              />
              <select value={paymentMethod} onChange={(e) => setPaymentMethod(e.target.value)}>
                <option value="credit_card">Credit Card</option>
                <option value="bank_transfer">Bank Transfer</option>
                <option value="cash_on_delivery">Cash on Delivery</option>
              </select>
              <button type="submit">Place Order</button>
            </form>
          )}
        </>
      )}
    </div>
  );
};

const Orders = ({ user }) => {
  const { loading, error, data } = useQuery(GET_MY_ORDERS);

  if (loading) return <p className="loading">Loading orders...</p>;
  if (error) return <p className="error">Error loading orders: {error.message}</p>;

  const orders = data?.myOrders || [];

  return (
    <div className="orders">
      <h2>My Orders</h2>
      {orders.length === 0 ? (
        <p>No orders found.</p>
      ) : (
        <div className="orders-list">
          {orders.map(order => (
            <div key={order.id} className="order-card">
              <div className="order-header">
                <h3>Order #{order.id}</h3>
                <span className={`status ${order.status.toLowerCase().replace(/ /g, '_')}`}>{order.status}</span>
              </div>
              <div className="order-details">
                <p><b>Total Amount:</b> Rp{order.total_amount.toFixed(2)}</p>
                <p><b>Shipping Address:</b> {order.shipping_address}</p>
                <p><b>Order Date:</b> {new Date(order.created_at).toLocaleString()}</p>

                <h4>Items:</h4>
                <div className="order-items-list">
                  {order.items.map(item => (
                    <div key={item.id} className="order-item-detail">
                      <img src={item.product.image_url || 'placeholder.png'} alt={item.product.name} className="order-item-image" />
                      <div className="item-info">
                        <p><b>{item.product.name}</b> x {item.quantity}</p>
                        <p>Price: Rp{(item.price * item.quantity).toFixed(2)}</p>
                      </div>
                    </div>
                  ))}
                </div>

                {order.tracking && (
                  <div className="tracking-info">
                    <h4>Tracking Information:</h4>
                    <p><b>Service ID:</b> {order.tracking.service_id}</p>
                    <p><b>Status:</b> <span className={`status ${order.tracking.status.toLowerCase().replace(/ /g, '_')}`}>{order.tracking.status}</span></p>
                    {order.tracking.estimated_delivery && (
                      <p><b>Estimated Delivery:</b> {new Date(order.tracking.estimated_delivery).toLocaleDateString()}</p>
                    )}
                    {order.tracking.actual_delivery && (
                      <p><b>Actual Delivery:</b> {new Date(order.tracking.actual_delivery).toLocaleDateString()}</p>
                    )}
                    {order.tracking.tracking_notes && (
                      <p><b>Notes:</b> {order.tracking.tracking_notes}</p>
                    )}
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

