          <button type="submit">Add Inventory Product</button>
        </form>
      )}

      <div className="inventory-list">
        <h3>Inventory Products</h3>
        <div className="inventory-grid">
          {inventoryData?.inventoryProducts.map(inventory => {
            const product = productsData?.products.find(p => p.id === inventory.product_id);
            const warehouse = warehousesData?.warehouses.find(w => w.id === inventory.warehouse_id); // Fixed: Use 'id' instead of 'id_warehouse'
            
            return (
              <div key={inventory.id} className="inventory-card"> {/* Fixed: Use 'id' instead of 'id_inventory' */}
                <div className="inventory-info">
                  <h4>{product?.name || 'Unknown Product'}</h4>
                  <p className="warehouse">Warehouse: {warehouse?.name || 'Unknown Warehouse'}</p>
