};

const AdminApproveOrders = ({ user }) => {
  const { loading, error, data } = useQuery(GET_ALL_ORDERS);
  const [updateOrderStatus, { loading: updatingStatus }] = useMutation(UPDATE_ORDER_STATUS, {
    onCompleted: () => {
      // Handle completion if needed
    }
  });
  const [updateDeliveryStatus, { loading: updatingDelivery }] = useMutation(UPDATE_DELIVERY_STATUS, {
    onCompleted: () => {
      // Handle completion if needed
    }
  });

  const [deliveryUpdates, setDeliveryUpdates] = useState({});

  if (!user || user.role !== 'admin') return <div>Admin access required</div>;
  if (loading) return <div className="loading">Loading orders...</div>;
  if (error) return <div className="error">Error: {error.message}</div>;

  // Filter for pending and processing orders for admin view
  const relevantOrders = data?.orders?.filter(o => o.status === 'pending' || o.status === 'processing') || [];

  const handleDeliveryUpdateChange = (orderId, field, value) => {
    setDeliveryUpdates(prev => ({
      ...prev,
      [orderId]: {
        ...prev[orderId],
        [field]: value
      }
    }));
  };

  const handleUpdateDelivery = async (order) => {
    const updateData = deliveryUpdates[order.id];
    if (!order.tracking?.service_id || !updateData?.status) {
      alert('Please select a new status for delivery.');
      return;
    }
    try {
      await updateDeliveryStatus({
        variables: {
          service_id: order.tracking.service_id,
          status: updateData.status,
          tracking_notes: updateData.tracking_notes || order.tracking.tracking_notes
        }
      });
      alert('Delivery status updated successfully!');
      setDeliveryUpdates(prev => { delete prev[order.id]; return {...prev}; }); // Clear update state for this order
    } catch (err) {
      console.error('Error updating delivery status:', err);
      alert('Failed to update delivery status: ' + err.message);
    }
  };

  return (
    <div className="admin-panel">
      <h2>Manage Orders</h2>
      {relevantOrders.length === 0 ? <p>No pending or processing orders.</p> : (
        <div className="orders-list">
          {relevantOrders.map(order => (
            <div key={order.id} className="order-card">
              <div className="order-header">
                <h3>Order #{order.id}</h3>
                <span className={`status ${order.status}`}>{order.status}</span>
              </div>
              <div className="order-details">
                <p>User: {order.user?.username || order.user_id}</p>
                <p>Total: ${order.total_amount}</p>
                <p>Date: {new Date(order.created_at).toLocaleDateString()}</p>
                <p>Address: {order.shipping_address}</p>
                
                {/* Delivery Tracking Info */}
                {order.tracking ? (
                  <div className="tracking-info">
                    <p><b>Tracking ID:</b> {order.tracking.service_id}</p>
                    <p><b>Delivery Status:</b> <span className={`status ${order.tracking.status.toLowerCase().replace(/ /g, '_')}`}>{order.tracking.status}</span></p>
                    {order.tracking.estimated_delivery && (
                      <p><b>Estimated Delivery:</b> {new Date(order.tracking.estimated_delivery).toLocaleDateString()}</p>
                    )}
                    {order.tracking.actual_delivery && (
                      <p><b>Actual Delivery:</b> {new Date(order.tracking.actual_delivery).toLocaleDateString()}</p>
                    )}
                    {order.tracking.tracking_notes && (
                      <p><b>Notes:</b> {order.tracking.tracking_notes}</p>
                    )}

                    {/* Delivery Status Update Form */}
                    <div className="delivery-update-form">
                      <h4>Update Delivery Status</h4>
                      <select
                        value={deliveryUpdates[order.id]?.status || ''}
                        onChange={(e) => handleDeliveryUpdateChange(order.id, 'status', e.target.value)}
                      >
                        <option value="">Select Status</option>
                        <option value="processing">Processing</option>
                        <option value="shipped">Shipped</option>
                        <option value="out_for_delivery">Out for Delivery</option>
                        <option value="delivered">Delivered</option>
                        <option value="cancelled">Cancelled</option>
                      </select>
                      <textarea
                        placeholder="Tracking Notes (optional)"
                        value={deliveryUpdates[order.id]?.tracking_notes || ''}
                        onChange={(e) => handleDeliveryUpdateChange(order.id, 'tracking_notes', e.target.value)}
                        rows="2"
                      />
                      <button
                        onClick={() => handleUpdateDelivery(order)}
                        disabled={updatingDelivery || !deliveryUpdates[order.id]?.status}
                      >
                        {updatingDelivery ? 'Updating...' : 'Update Tracking'}
                      </button>
                    </div>
                  </div>
                ) : (
                  <p>No tracking information available for this order yet.</p>
                )}

                {/* Order Status Update Button (if order is still pending) */}
                {order.status === 'pending' && (
                  <button
                    onClick={() => updateOrderStatus({ variables: { id: order.id, status: 'processing' } })}
                    disabled={updatingStatus}
                    style={{ marginTop: 10 }}
                  >
                    {updatingStatus ? 'Processing...' : 'Approve Order'}
                  </button>
                )}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

const Profile = ({ user }) => {
  const [username, setUsername] = useState(user.username);
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [updateProfile, { loading, error }] = useMutation(UPDATE_PROFILE);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (password && password !== confirmPassword) {
      alert('Passwords do not match!');
      return;
    }
    try {
      await updateProfile({
        variables: {
          username: username !== user.username ? username : undefined,
          password: password || undefined
        }
      });
      alert('Profile updated successfully!');
      setPassword('');
      setConfirmPassword('');
    } catch (err) {
      console.error('Error updating profile:', err);
      alert('Failed to update profile');
    }
  };

  return (
    <div className="profile-container">
      <h2>Profile Settings</h2>
      <form onSubmit={handleSubmit} className="profile-form">
        <div className="form-group">
          <label>Username:</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="form-control"
          />
        </div>
        <div className="form-group">
          <label>New Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="form-control"
            placeholder="Leave blank to keep current password"
          />
        </div>
        <div className="form-group">
          <label>Confirm New Password:</label>
          <input
            type="password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            className="form-control"
            placeholder="Confirm new password"
          />
        </div>
        <button type="submit" className="btn btn-primary" disabled={loading}>
          {loading ? 'Updating...' : 'Update Profile'}
        </button>
        {error && <div className="error-message">{error.message}</div>}
      </form>
    </div>
  );
};

// Main App Component
const AppContent = () => {
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('user')));
  const [currentPage, setCurrentPage] = useState('products');
  const [showLogin, setShowLogin] = useState(true);
  const [toast, setToast] = useState('');

  const showToast = (msg) => setToast(msg);

  useEffect(() => {
    if (toast) {
      const timer = setTimeout(() => setToast(''), 3000);
      return () => clearTimeout(timer);
    }
  }, [toast]);

  const handleLogin = (userData) => {
    setUser(userData);
    setCurrentPage('products');
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    setCurrentPage('products');
  };

  const renderContent = () => {
    if (!user) {
      return showLogin ? 
        <LoginForm onLogin={handleLogin} onToggleMode={() => setShowLogin(!showLogin)} /> :
        <RegisterForm onLogin={handleLogin} onToggleMode={() => setShowLogin(!showLogin)} />;
    }

    switch (currentPage) {
      case 'products':
        return <ProductList user={user} setCurrentPage={setCurrentPage} showToast={showToast} />;
      case 'cart':
        if (user?.role !== 'customer') return <div>Only customers can access cart.</div>;
        return <Cart user={user} />;
      case 'orders':
        if (user?.role !== 'customer') return <div>Only customers can access orders.</div>;
        return <Orders user={user} />;
      case 'profile':
        return <Profile user={user} />;
      case 'admin':
        if (user?.role !== 'admin') return <div>Only admins can access admin panel.</div>;
        return <AdminPanel user={user} />;
      case 'warehouses':
        if (user?.role !== 'admin') return <div>Only admins can access warehouses.</div>;
        return <WarehouseManagement user={user} />;
      case 'inventory':
        if (user?.role !== 'admin') return <div>Only admins can access inventory.</div>;
        return <InventoryManagement user={user} />;
      case 'approve-orders':
        if (user?.role !== 'admin') return <div>Only admins can approve orders.</div>;
        return <AdminApproveOrders user={user} />;
      case 'all-orders':
        if (user?.role !== 'admin') return <div>Only admins can access all orders.</div>;
        return <AdminAllOrders user={user} />;
      case 'delivery-tracking':
        return <DeliveryTrackingPage />;
      default:
        return <ProductList user={user} setCurrentPage={setCurrentPage} showToast={showToast} />;
    }
  };

  return (
    <div className="app">
      <header className="app-header">
        <h1>belilokal.id</h1>
        {user && (
          <nav className="nav-menu">
            <button
              className={currentPage === 'products' ? 'active' : ''}
              onClick={() => setCurrentPage('products')}
            >
              Products
            </button>
            {user.role === 'customer' && (
              <>
                <button
                  className={currentPage === 'cart' ? 'active' : ''}
                  onClick={() => setCurrentPage('cart')}
                >
                  Cart
                </button>
                <button
                  className={currentPage === 'orders' ? 'active' : ''}
                  onClick={() => setCurrentPage('orders')}
                >
                  My Orders
                </button>
              </>
            )}
            {user.role === 'admin' && (
              <>
                <button
                  className={currentPage === 'admin' ? 'active' : ''}
                  onClick={() => setCurrentPage('admin')}
                >
                  Admin Panel
                </button>
                <button
                  className={currentPage === 'warehouses' ? 'active' : ''}
                  onClick={() => setCurrentPage('warehouses')}
                >
                  Warehouses
                </button>
                <button
                  className={currentPage === 'inventory' ? 'active' : ''}
                  onClick={() => setCurrentPage('inventory')}
                >
                  Inventory
                </button>
                <button
                  className={currentPage === 'all-orders' ? 'active' : ''}
                  onClick={() => setCurrentPage('all-orders')}
                >
                  All Orders
                </button>
              </>
            )}
            <button
              className={currentPage === 'profile' ? 'active' : ''}
              onClick={() => setCurrentPage('profile')}
            >
              Profile
            </button>
            <button
              className={currentPage === 'delivery-tracking' ? 'active' : ''}
              onClick={() => setCurrentPage('delivery-tracking')}
            >
              Track Delivery
            </button>
            <button onClick={handleLogout} className="logout-btn">
              Logout
            </button>
          </nav>
        )}
      </header>
      <main className="main-content">
        {renderContent()}
      </main>
      {toast && <Toast message={toast} onClose={() => setToast('')} />}
    </div>
  );
};

function App() {
  return (
    <ApolloProvider client={client}>
      <Router>
        <Routes>
          <Route path="/register" element={<Register />} />
          <Route path="/" element={<AppContent />} />
        </Routes>
      </Router>
    </ApolloProvider>
  );
}

export default App;
