import React, { useState } from 'react';
import { gql, useLazyQuery } from '@apollo/client';
import '../styles/DeliveryTracking.css';

const TRACK_DELIVERY = gql`
  query TrackDelivery($service_id: String!) {
    trackDelivery(service_id: $service_id) {
      service_id
      status
      estimated_delivery
      actual_delivery
      tracking_notes
      created_at
      updated_at
    }
  }
`;

const DeliveryTrackingPage = () => {
  const [serviceId, setServiceId] = useState('');
  const [trackDelivery, { loading, data, error, called }] = useLazyQuery(TRACK_DELIVERY);
  const [searchHistory, setSearchHistory] = useState([]);

  const handleTrack = (e) => {
    e.preventDefault();
    if (serviceId.trim()) {
      const trimmedId = serviceId.trim();
      trackDelivery({ variables: { service_id: trimmedId } });
      
      // Add to search history (keep last 5 searches)
      setSearchHistory(prev => {
        const newHistory = [trimmedId, ...prev.filter(id => id !== trimmedId)];
        return newHistory.slice(0, 5);
      });
    }
  };

  const handleQuickSearch = (id) => {
    setServiceId(id);
    trackDelivery({ variables: { service_id: id } });
  };

  const getStatusInfo = (status) => {
    const statusMap = {
      pending: {
        color: '#f59e0b',
        icon: '⏳',
        message: 'Your order is being prepared'
      },
      processing: {
        color: '#3b82f6',
        icon: '🔄',
        message: 'Order is being processed'
      },
      shipped: {
        color: '#8b5cf6',
        icon: '📦',
        message: 'Package has been shipped'
      },
      out_for_delivery: {
        color: '#f97316',
        icon: '🚚',
        message: 'Out for delivery'
      },
      delivered: {
        color: '#10b981',
        icon: '✅',
        message: 'Successfully delivered'
      },
      failed: {
        color: '#ef4444',
        icon: '❌',
        message: 'Delivery failed'
      },
      cancelled: {
        color: '#6b7280',
        icon: '🚫',
        message: 'Delivery cancelled'
      }
    };
    
    return statusMap[status] || {
      color: '#6b7280',
      icon: '❓',
      message: 'Status unknown'
    };
  };

  const formatDate = (dateString) => {
    if (!dateString) return "Not available";
    
    try {
      const date = new Date(dateString);
      if (isNaN(date.getTime())) return "Invalid date";
      
      return date.toLocaleDateString('en-US', {
        year: 'numeric',
        month: 'long',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch (error) {
      return "Invalid date format";
    }
  };

  const getDeliveryProgress = (status) => {
    const progressMap = {
      pending: 20,
      processing: 40,
      shipped: 60,
      out_for_delivery: 80,
      delivered: 100,
      failed: 0,
      cancelled: 0
    };
    
    return progressMap[status] || 0;
  };

  const renderTrackingTimeline = (trackingData) => {
    const timeline = [
      { status: 'pending', label: 'Order Placed', icon: '📝' },
      { status: 'processing', label: 'Processing', icon: '🔄' },
      { status: 'shipped', label: 'Shipped', icon: '📦' },
      { status: 'out_for_delivery', label: 'Out for Delivery', icon: '🚚' },
      { status: 'delivered', label: 'Delivered', icon: '✅' }
    ];

    const currentStatusIndex = timeline.findIndex(item => item.status === trackingData.status);
    
    return (
      <div className="tracking-timeline">
        {timeline.map((item, index) => {
          const isCompleted = index <= currentStatusIndex;
          const isCurrent = index === currentStatusIndex;
          
          return (
            <div 
              key={item.status} 
              className={`timeline-item ${isCompleted ? 'completed' : ''} ${isCurrent ? 'current' : ''}`}
            >
              <div className="timeline-icon">
                {item.icon}
              </div>
              <div className="timeline-content">
                <div className="timeline-label">{item.label}</div>
                {isCurrent && trackingData.updated_at && (
                  <div className="timeline-time">
                    {formatDate(trackingData.updated_at)}
                  </div>
                )}
              </div>
              {index < timeline.length - 1 && (
                <div className={`timeline-connector ${isCompleted ? 'completed' : ''}`} />
              )}
            </div>
          );
        })}
      </div>
    );
  };

  return (
    <div className="delivery-tracking-page">
      <div className="section-header">
        <h2>Track Your Delivery</h2>
        <p>Enter your tracking ID to get real-time delivery updates</p>
      </div>

      {/* Search Form */}
      <div className="tracking-search">
        <form onSubmit={handleTrack} className="search-form">
          <div className="search-input-group">
            <input
              type="text"
              placeholder="Enter your tracking ID (e.g., TRK123456789)"
              value={serviceId}
              onChange={(e) => setServiceId(e.target.value)}
              className="search-input"
              required
            />
            <button 
              type="submit" 
              className="search-button"
              disabled={loading || !serviceId.trim()}
            >
              {loading ? (
                <div className="button-spinner"></div>
              ) : (
                <>
                  <span className="search-icon">🔍</span>
                  Track Package
                </>
              )}
            </button>
          </div>
        </form>

        {/* Search History */}
        {searchHistory.length > 0 && (
          <div className="search-history">
            <h4>Recent Searches:</h4>
            <div className="history-tags">
              {searchHistory.map((id, index) => (
                <button
                  key={index}
                  onClick={() => handleQuickSearch(id)}
                  className="history-tag"
                  disabled={loading}
                >
                  {id}
                </button>
              ))}
            </div>
          </div>
        )}
      </div>

      {/* Loading State */}
      {loading && (
        <div className="tracking-loading">
          <div className="loading-spinner">
            <div className="spinner"></div>
            <p>Tracking your package...</p>
          </div>
        </div>
      )}

      {/* Error State */}
      {called && error && (
        <div className="tracking-error">
          <div className="error-icon">⚠️</div>
          <h3>Tracking Error</h3>
          <p>{error.message}</p>
          <button onClick={() => handleTrack({ preventDefault: () => {} })} className="retry-button">
            Try Again
          </button>
        </div>
      )}

      {/* No Results */}
      {called && !loading && !error && !data?.trackDelivery && (
        <div className="tracking-not-found">
          <div className="not-found-icon">📦❓</div>
          <h3>Tracking Information Not Found</h3>
          <p>
            We couldn't find any tracking information for ID: <strong>{serviceId}</strong>
          </p>
          <div className="help-text">
            <h4>Please check:</h4>
            <ul>
              <li>The tracking ID is correct and complete</li>
              <li>The tracking ID has been activated (may take a few hours after order placement)</li>
              <li>You're using the correct tracking ID from your order confirmation</li>
            </ul>
          </div>
        </div>
      )}

      {/* Tracking Results */}
      {called && !loading && !error && data?.trackDelivery && (
        <div className="tracking-results">
          <div className="tracking-header">
            <div className="tracking-id-display">
              <h3>Package Status</h3>
              <div className="tracking-id-badge">
                Tracking ID: {data.trackDelivery.service_id}
              </div>
            </div>
            
            <div className="status-display">
              {(() => {
                const statusInfo = getStatusInfo(data.trackDelivery.status);
                return (
                  <div 
                    className="status-card"
                    style={{ backgroundColor: statusInfo.color + '20', borderColor: statusInfo.color }}
                  >
                    <div className="status-icon">{statusInfo.icon}</div>
                    <div className="status-text">
                      <div className="status-name">{data.trackDelivery.status.replace('_', ' ').toUpperCase()}</div>
                      <div className="status-message">{statusInfo.message}</div>
                    </div>
                  </div>
                );
              })()}
            </div>
          </div>

          {/* Progress Bar */}
          <div className="progress-section">
            <div className="progress-bar">
              <div 
                className="progress-fill"
                style={{ width: `${getDeliveryProgress(data.trackDelivery.status)}%` }}
              />
            </div>
            <div className="progress-text">
              {getDeliveryProgress(data.trackDelivery.status)}% Complete
            </div>
          </div>

          {/* Timeline */}
          <div className="timeline-section">
            <h4>Delivery Timeline</h4>
            {renderTrackingTimeline(data.trackDelivery)}
          </div>

          {/* Delivery Details */}
          <div className="delivery-details">
            <h4>Delivery Information</h4>
            <div className="details-grid">
              {data.trackDelivery.estimated_delivery && (
                <div className="detail-item">
                  <div className="detail-label">Estimated Delivery</div>
                  <div className="detail-value">
                    {formatDate(data.trackDelivery.estimated_delivery)}
                  </div>
                </div>
              )}
              
              {data.trackDelivery.actual_delivery && (
                <div className="detail-item">
                  <div className="detail-label">Delivered On</div>
                  <div className="detail-value delivered">
                    {formatDate(data.trackDelivery.actual_delivery)}
                  </div>
                </div>
              )}
              
              <div className="detail-item">
                <div className="detail-label">Last Updated</div>
                <div className="detail-value">
                  {formatDate(data.trackDelivery.updated_at)}
                </div>
              </div>
            </div>

            {data.trackDelivery.tracking_notes && (
              <div className="tracking-notes">
                <div className="notes-label">Delivery Notes</div>
                <div className="notes-content">
                  {data.trackDelivery.tracking_notes}
                </div>
              </div>
            )}
          </div>

          {/* Action Buttons */}
          <div className="tracking-actions">
            <button 
              onClick={() => window.print()} 
              className="action-button print-button"
            >
              🖨️ Print Details
            </button>
            <button 
              onClick={() => navigator.share && navigator.share({
                title: 'Package Tracking',
                text: `Track package ${data.trackDelivery.service_id}`,
                url: window.location.href
              })}
              className="action-button share-button"
            >
              📤 Share Tracking
            </button>
          </div>

          {/* Refresh Button */}
          <div className="refresh-section">
            <button 
              onClick={() => trackDelivery({ variables: { service_id: data.trackDelivery.service_id } })}
              className="refresh-button"
              disabled={loading}
            >
              🔄 Refresh Status
            </button>
            <p className="refresh-note">
              Tracking information is updated every few hours
            </p>
          </div>
        </div>
      )}

      {/* Help Section */}
      <div className="help-section">
        <h4>Need Help?</h4>
        <div className="help-grid">
          <div className="help-item">
            <h5>Where to find your tracking ID?</h5>
            <p>Your tracking ID is included in your order confirmation email and can be found in your order history.</p>
          </div>
          <div className="help-item">
            <h5>Tracking not updating?</h5>
            <p>Tracking information may take 24-48 hours to appear after your order ships. Please check back later.</p>
          </div>
          <div className="help-item">
            <h5>Delivery issues?</h5>
            <p>If you're experiencing delivery problems, please contact our customer support team.</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DeliveryTrackingPage;
