import React, { useState, useEffect } from 'react';
import { gql, useLazyQuery } from '@apollo/client';
import '../styles/DeliveryTracking.css';

const TRACK_DELIVERY = gql`
  query TrackDelivery($service_id: String!) {
    trackDelivery(service_id: $service_id) {
      service_id
      status
      estimated_delivery
      actual_delivery
      tracking_notes
      created_at
      updated_at
      order_id
    }
  }
`;

const DeliveryTrackingPage = () => {
  const [serviceId, setServiceId] = useState('');
  const [trackDelivery, { loading, data, error, called }] = useLazyQuery(TRACK_DELIVERY);
  const [searchHistory, setSearchHistory] = useState([]);

  // Check for prefilled tracking ID on component mount
  useEffect(() => {
    const prefilledTrackingId = localStorage.getItem('prefilledTrackingId');
    if (prefilledTrackingId) {
      setServiceId(prefilledTrackingId);
      // Auto-track if there's a prefilled ID
      trackDelivery({ variables: { service_id: prefilledTrackingId } });
      // Clear the prefilled ID after using it
      localStorage.removeItem('prefilledTrackingId');
    }
    
    // Load search history from localStorage
    const savedHistory = localStorage.getItem('trackingSearchHistory');
    if (savedHistory) {
      try {
        setSearchHistory(JSON.parse(savedHistory));
      } catch (e) {
        console.error('Error loading search history:', e);
      }
    }
  }, [trackDelivery]);

  // Save search history to localStorage whenever it changes
  useEffect(() => {
    if (searchHistory.length > 0) {
      localStorage.setItem('trackingSearchHistory', JSON.stringify(searchHistory));
    }
  }, [searchHistory]);

  const handleTrack = (e) => {
    e.preventDefault();
    if (serviceId.trim()) {
      const trimmedId = serviceId.trim();
      trackDelivery({ variables: { service_id: trimmedId } });
      
      // Add to search history (keep last 5 searches)
      setSearchHistory(prev => {
        const newHistory = [trimmedId, ...prev.filter(id => id !== trimmedId)];
        return newHistory.slice(0, 5);
      });
    }
  };

  const handleQuickSearch = (id) => {
    setServiceId(id);
    trackDelivery({ variables: { service_id: id } });
  };

  const clearSearchHistory = () => {
    setSearchHistory([]);
    localStorage.removeItem('trackingSearchHistory');
  };

  const getStatusInfo = (status) => {
    const statusMap = {
      pending: {
        color: '#f59e0b',
        icon: '⏳',
        message: 'Your order is being prepared',
        progress: 20
      },
      processing: {
        color: '#3b82f6',
        icon: '🔄',
        message: 'Order is being processed',
        progress: 40
      },
      shipped: {
        color: '#8b5cf6',
        icon: '📦',
        message: 'Package has been shipped',
        progress: 60
      },
      out_for_delivery: {
        color: '#f97316',
        icon: '🚚',
        message: 'Out for delivery',
        progress: 80
      },
      delivered: {
        color: '#10b981',
        icon: '✅',
        message: 'Successfully delivered',
        progress: 100
      },
      failed: {
        color: '#ef4444',
        icon: '❌',
        message: 'Delivery failed',
        progress: 0
      },
      cancelled: {
        color: '#6b7280',
        icon: '🚫',
        message: 'Delivery cancelled',
        progress: 0
      }
    };
    
    return statusMap[status] || {
      color: '#6b7280',
      icon: '❓',
      message: 'Status unknown',
      progress: 0
    };
  };

  const formatDate = (dateString) => {
    if (!dateString) return "Not available";
    
    try {
      const date = new Date(dateString);
      if (isNaN(date.getTime())) return "Invalid date";
      
      return date.toLocaleDateString('en-US', {
        year: 'numeric',
        month: 'long',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch (error) {
      return "Invalid date format";
    }
  };

  const getDeliveryProgress = (status) => {
    const statusInfo = getStatusInfo(status);
    return statusInfo.progress;
  };

  const renderTrackingTimeline = (trackingData) => {
    const timeline = [
      { status: 'pending', label: 'Order Placed', icon: '📝' },
      { status: 'processing', label: 'Processing', icon: '🔄' },
      { status: 'shipped', label: 'Shipped', icon: '📦' },
      { status: 'out_for_delivery', label: 'Out for Delivery', icon: '🚚' },
      { status: 'delivered', label: 'Delivered', icon: '✅' }
    ];

    const currentStatusIndex = timeline.findIndex(item => item.status === trackingData.status);
    
    return (
      <div className="tracking-timeline">
        {timeline.map((item, index) => {
          const isCompleted = index <= currentStatusIndex && trackingData.status !== 'failed' && trackingData.status !== 'cancelled';
          const isCurrent = index === currentStatusIndex && trackingData.status !== 'failed' && trackingData.status !== 'cancelled';
          
          return (
            <div 
              key={item.status} 
              className={`timeline-item ${isCompleted ? 'completed' : ''} ${isCurrent ? 'current' : ''}`}
            >
              <div className="timeline-icon">
                {item.icon}
              </div>
              <div className="timeline-content">
                <div className="timeline-label">{item.label}</div>
                {isCurrent && trackingData.updated_at && (
                  <div className="timeline-time">
                    {formatDate(trackingData.updated_at)}
                  </div>
                )}
              </div>
              {index < timeline.length - 1 && (
                <div className={`timeline-connector ${isCompleted ? 'completed' : ''}`} />
              )}
            </div>
          );
        })}
      </div>
    );
  };

  return (
    <div className="delivery-tracking-page">
      <div className="section-header">
        <h2>Track Your Delivery</h2>
        <p>Enter your tracking ID to get real-time delivery updates</p>
      </div>

      {/* Search Form */}
      <div className="tracking-search">
        <form onSubmit={handleTrack} className="search-form">
          <div className="search-input-group">
            <input
              type="text"
              placeholder="Enter your tracking ID (e.g., TRK123456789)"
              value={serviceId}
              onChange={(e) => setServiceId(e.target.value)}
              className="search-input"
              required
            />
            <button 
              type="submit" 
              className="search-button"
              disabled={loading || !serviceId.trim()}
            >
              {loading ? (
                <div className="button-spinner"></div>
              ) : (
                <>
                  <span className="search-icon">🔍</span>
                  Track Package
                </>
              )}
            </button>
          </div>
        </form>

        {/* Search History */}
        {searchHistory.length > 0 && (
          <div className="search-history">
            <div className="history-header">
              <h4>Recent Searches:</h4>
              <button 
                onClick={clearSearchHistory}
                className="clear-history-btn"
                title="Clear search history"
              >
                🗑️ Clear
              </button>
            </div>
            <div className="history-tags">
              {searchHistory.map((id, index) => (
                <button
                  key={index}
                  onClick={() => handleQuickSearch(id)}
                  className="history-tag"
                  disabled={loading}
                >
                  {id}
                </button>
              ))}
            </div>
          </div>
        )}
      </div>

      {/* Loading State */}
      {loading && (
        <div className="tracking-loading">
          <div className="loading-spinner">
            <div className="spinner"></div>
            <p>Tracking your package...</p>
          </div>
        </div>
      )}

      {/* Error State */}
      {called && error && (
        <div className="tracking-error">
          <div className="error-icon">⚠️</div>
          <h3>Tracking Error</h3>
          <p>{error.message}</p>
          <div className="error-details">
            <p>This could happen if:</p>
            <ul>
              <li>The tracking ID is incorrect</li>
              <li>The package information hasn't been updated yet</li>
              <li>There's a temporary system issue</li>
            </ul>
          </div>
          <button onClick={() => handleTrack({ preventDefault: () => {} })} className="retry-button">
            Try Again
          </button>
        </div>
      )}

      {/* No Results */}
      {called && !loading && !error && !data?.trackDelivery && (
        <div className="tracking-not-found">
          <div className="not-found-icon">📦❓</div>
          <h3>Tracking Information Not Found</h3>
          <p>
            We couldn't find any tracking information for ID: <strong>{serviceId}</strong>
          </p>
          <div className="help-text">
            <h4>Please check:</h4>
            <ul>
              <li>The tracking ID is correct and complete</li>
              <li>The tracking ID has been activated (may take a few hours after order placement)</li>
              <li>You're using the correct tracking ID from your order confirmation</li>
            </ul>
            <div className="support-info">
              <p><strong>Need help?</strong> Your tracking ID should start with "TRK" followed by numbers.</p>
              <p>If you're still having trouble, please contact customer support.</p>
            </div>
          </div>
        </div>
      )}

      {/* Tracking Results */}
      {called && !loading && !error && data?.trackDelivery && (
        <div className="tracking-results">
          <div className="tracking-header">
            <div className="tracking-id-display">
              <h3>Package Status</h3>
              <div className="tracking-id-badge">
                Tracking ID: {data.trackDelivery.service_id}
              </div>
              {data.trackDelivery.order_id && (
                <div className="order-id-badge">
                  Order ID: #{data.trackDelivery.order_id}
                </div>
              )}
            </div>
            
            <div className="status-display">
              {(() => {
                const statusInfo = getStatusInfo(data.trackDelivery.status);
                return (
                  <div 
                    className="status-card"
                    style={{ backgroundColor: statusInfo.color + '20', borderColor: statusInfo.color }}
                  >
                    <div className="status-icon">{statusInfo.icon}</div>
                    <div className="status-text">
                      <div className="status-name">{data.trackDelivery.status.replace('_', ' ').toUpperCase()}</div>
                      <div className="status-message">{statusInfo.message}</div>
                    </div>
                  </div>
                );
              })()}
            </div>
          </div>

          {/* Progress Bar */}
          <div className="progress-section">
            <div className="progress-bar">
              <div 
                className="progress-fill"
                style={{ width: `${getDeliveryProgress(data.trackDelivery.status)}%` }}
              />
            </div>
            <div className="progress-text">
              {getDeliveryProgress(data.trackDelivery.status)}% Complete
            </div>
          </div>

          {/* Timeline - Only show for non-failed/non-cancelled deliveries */}
          {data.trackDelivery.status !== 'failed' && data.trackDelivery.status !== 'cancelled' && (
            <div className="timeline-section">
              <h4>Delivery Timeline</h4>
              {renderTrackingTimeline(data.trackDelivery)}
            </div>
          )}

          {/* Delivery Details */}
          <div className="delivery-details">
            <h4>Delivery Information</h4>
            <div className="details-grid">
              <div className="detail-item">
                <div className="detail-label">Order Placed</div>
                <div className="detail-value">
                  {formatDate(data.trackDelivery.created_at)}
                </div>
              </div>

              {data.trackDelivery.estimated_delivery && (
                <div className="detail-item">
                  <div className="detail-label">Estimated Delivery</div>
                  <div className="detail-value">
                    {formatDate(data.trackDelivery.estimated_delivery)}
                  </div>
                </div>
              )}
              
              {data.trackDelivery.actual_delivery && (
                <div className="detail-item">
                  <div className="detail-label">Delivered On</div>
                  <div className="detail-value delivered">
                    {formatDate(data.trackDelivery.actual_delivery)}
                  </div>
                </div>
              )}
              
              <div className="detail-item">
                <div className="detail-label">Last Updated</div>
                <div className="detail-value">
                  {formatDate(data.trackDelivery.updated_at)}
                </div>
              </div>
            </div>

            {data.trackDelivery.tracking_notes && (
              <div className="tracking-notes">
                <div className="notes-label">Delivery Notes</div>
                <div className="notes-content">
                  {data.trackDelivery.tracking_notes}
                </div>
              </div>
            )}
          </div>

          {/* Action Buttons */}
          <div className="tracking-actions">
            <button 
              onClick={() => {
                const printContent = `
                  Tracking ID: ${data.trackDelivery.service_id}
                  Status: ${data.trackDelivery.status.replace('_', ' ').toUpperCase()}
                  ${data.trackDelivery.estimated_delivery ? `Estimated Delivery: ${formatDate(data.trackDelivery.estimated_delivery)}` : ''}
                  ${data.trackDelivery.actual_delivery ? `Delivered On: ${formatDate(data.trackDelivery.actual_delivery)}` : ''}
                  Last Updated: ${formatDate(data.trackDelivery.updated_at)}
                  ${data.trackDelivery.tracking_notes ? `Notes: ${data.trackDelivery.tracking_notes}` : ''}
                `;
                const printWindow = window.open('', '_blank');
                printWindow.document.write(`<pre>${printContent}</pre>`);
                printWindow.print();
              }}
              className="action-button print-button"
            >
              🖨️ Print Details
            </button>
            
            <button 
              onClick={() => {
                if (navigator.share) {
                  navigator.share({
                    title: 'Package Tracking',
                    text: `Track package ${data.trackDelivery.service_id}`,
                    url: window.location.href
                  });
                } else {
                  // Fallback for browsers that don't support Web Share API
                  const trackingUrl = `${window.location.origin}${window.location.pathname}?track=${data.trackDelivery.service_id}`;
                  navigator.clipboard.writeText(trackingUrl).then(() => {
                    alert('Tracking link copied to clipboard!');
                  }).catch(() => {
                    alert('Unable to copy link. Please copy manually: ' + trackingUrl);
                  });
                }
              }}
              className="action-button share-button"
            >
              📤 Share Tracking
            </button>
          </div>

          {/* Refresh Button */}
          <div className="refresh-section">
            <button 
              onClick={() => trackDelivery({ variables: { service_id: data.trackDelivery.service_id } })}
              className="refresh-button"
              disabled={loading}
            >
              🔄 Refresh Status
            </button>
            <p className="refresh-note">
              Tracking information is updated automatically. Last update: {formatDate(data.trackDelivery.updated_at)}
            </p>
          </div>
        </div>
      )}

      {/* Help Section */}
      <div className="help-section">
        <h4>Need Help?</h4>
        <div className="help-grid">
          <div className="help-item">
            <h5>Where to find your tracking ID?</h5>
            <p>Your tracking ID is included in your order confirmation and can be found in your "My Orders" section. It usually starts with "TRK" followed by numbers.</p>
          </div>
          <div className="help-item">
            <h5>Tracking not updating?</h5>
            <p>Tracking information may take 24-48 hours to appear after your order ships. The system updates automatically every few hours.</p>
          </div>
          <div className="help-item">
            <h5>Delivery issues?</h5>
            <p>If you're experiencing delivery problems or have questions about your package, please contact our customer support team for assistance.</p>
          </div>
          <div className="help-item">
            <h5>Package not delivered?</h5>
            <p>If your tracking shows "delivered" but you haven't received your package, please check with neighbors and contact support within 48 hours.</p>
          </div>
        </div>
      </div>
