import React, { useState, useEffect } from 'react';
import { ApolloClient, InMemoryCache, ApolloProvider, createHttpLink, gql, useQuery, useMutation } from '@apollo/client';
import { setContext } from '@apollo/client/link/context';
import './App.css';
import AdminAllOrders from './components/AdminAllOrders';
import DeliveryTrackingPage from './components/DeliveryTrackingPage';
import Profile from './components/Profile';
import Cart from './components/Cart';
import Orders from './components/Orders';

// Date utility functions - inline to avoid import issues
const formatDate = (dateInput, options = {}) => {
  if (!dateInput) return "Not available";
  
  try {
    let date;
    
    if (typeof dateInput === 'string') {
      if (dateInput.includes('T') || dateInput.includes(' ')) {
        date = new Date(dateInput);
      } else {
        date = new Date(dateInput);
      }
    } else if (typeof dateInput === 'number') {
      date = new Date(dateInput);
    } else if (dateInput instanceof Date) {
      date = dateInput;
    } else {
      return "Invalid date format";
    }
    
    if (isNaN(date.getTime())) {
      console.warn('Invalid date input:', dateInput);
      return "Invalid date";
    }
    
    const defaultOptions = {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
      hour12: true,
      ...options
    };
    
    return date.toLocaleDateString('en-US', defaultOptions);
  } catch (error) {
    console.error('Error formatting date:', error);
    return "Invalid date format";
  }
};

const formatOrderDate = (dateInput) => {
  return formatDate(dateInput, {
    year: 'numeric',
    month: 'short',
    day: 'numeric',
    hour: '2-digit',
    minute: '2-digit'
  });
};

const formatAdminDate = (dateInput) => {
  return formatDate(dateInput, {
    year: 'numeric',
    month: 'numeric',
    day: 'numeric',
    hour: '2-digit',
    minute: '2-digit',
    second: '2-digit'
  });
};

// GraphQL Client Setup
const httpLink = createHttpLink({
  uri: 'http://localhost:4000/graphql', // Updated to use API Gateway
});

const authLink = setContext((_, { headers }) => {
  const token = localStorage.getItem('token');
  return {
    headers: {
      ...headers,
      authorization: token ? `Bearer ${token}` : "",
    }
  };
});

const client = new ApolloClient({
  link: authLink.concat(httpLink),
  cache: new InMemoryCache()
});

// GraphQL Queries and Mutations - FIXED PARAMETER TYPES
const GET_PRODUCTS = gql`
  query GetProducts($limit: Int, $offset: Int, $category_id: Int) {
    products(limit: $limit, offset: $offset, category_id: $category_id) {
      id
      name
      description
      price
      stock
      image_url
      category {
        name
      }
    }
  }
`;

const GET_CATEGORIES = gql`
  query GetCategories {
    categories {
      id
      name
      description
    }
  }
`;

const LOGIN_MUTATION = gql`
  mutation Login($email: String!, $password: String!) {
    login(email: $email, password: $password) {
      token
      user {
        id
        username
        email
        role
        fullName
        address
        phoneNumber
      }
    }
  }
`;

const REGISTER_MUTATION = gql`
  mutation Register($username: String!, $email: String!, $password: String!, $role: String) {
    register(username: $username, email: $email, password: $password, role: $role) {
      token
      user {
        id
        username
        email
        role
        fullName
        address
        phoneNumber
      }
    }
  }
`;

const GET_MY_CART = gql`
  query GetMyCart {
    myCart {
      id
      quantity
      product {
        id
        name
        price
        image_url
        stock
      }
    }
  }
`;

// FIXED: Changed product_id from Int! to ID! to match backend schema
const ADD_TO_CART = gql`
  mutation AddToCart($product_id: ID!, $quantity: Int!) {
    addToCart(product_id: $product_id, quantity: $quantity) {
      id
      quantity
      product {
        id
        name
        price
        image_url
        stock
      }
    }
  }
`;

const CREATE_ORDER = gql`
  mutation CreateOrder($shipping_address: String!, $payment_method: String!) {
    createOrder(shipping_address: $shipping_address, payment_method: $payment_method) {
      id
      total_amount
      status
      created_at
      tracking {
        service_id
        status
        estimated_delivery
        tracking_notes
      }
    }
  }
`;

const GET_MY_ORDERS = gql`
  query GetMyOrders {
    myOrders {
      id
      total_amount
      status
      shipping_address
      created_at
      updated_at
      items {
        id
        quantity
        price
        product {
          id
          name
          image_url
        }
      }
      tracking {
        service_id
        status
        estimated_delivery
        actual_delivery
        tracking_notes
      }
    }
  }
`;

const ADD_PRODUCT = gql`
  mutation AddProduct($name: String!, $description: String, $price: Float!, $category_id: Int!, $stock: Int!, $image_url: String, $initialWarehouseId: Int, $initialQuantity: Int) {
    addProduct(name: $name, description: $description, price: $price, category_id: $category_id, stock: $stock, image_url: $image_url, initialWarehouseId: $initialWarehouseId, initialQuantity: $initialQuantity) {
      id
      name
      price
      stock
    }
  }
`;

const GET_WAREHOUSES = gql`
  query GetWarehouses {
    warehouses {
      id_warehouse
      name
      location
      manager_id
      created_at
      updated_at
    }
  }
`;

const ADD_WAREHOUSE = gql`
  mutation AddWarehouse($name: String!, $location: String, $manager_id: Int) {
    addWarehouse(name: $name, location: $location, manager_id: $manager_id) {
      id_warehouse
      name
      location
      manager_id
      created_at
    }
  }
`;

const GET_INVENTORY_PRODUCTS = gql`
  query GetInventoryProducts {
    inventoryProducts {
      id_inventory
      product_id
      id_warehouse
      quantity
      reserved
      restock_threshold
      last_restocked
      updated_at
    }
  }
`;

const ADD_INVENTORY_PRODUCT = gql`
  mutation AddInventoryProduct($product_id: Int!, $id_warehouse: Int!, $quantity: Int!, $reserved: Int, $restock_threshold: Int, $last_restocked: String) {
    addInventoryProduct(product_id: $product_id, id_warehouse: $id_warehouse, quantity: $quantity, reserved: $reserved, restock_threshold: $restock_threshold, last_restocked: $last_restocked) {
      id_inventory
      product_id
      id_warehouse
      quantity
      reserved
      restock_threshold
      last_restocked
    }
  }
`;

const UPDATE_ORDER_STATUS = gql`
  mutation UpdateOrderStatus($id: Int!, $status: String!) {
    updateOrderStatus(id: $id, status: $status) {
      id
      status
    }
  }
`;

const UPDATE_DELIVERY_STATUS = gql`
  mutation UpdateDeliveryStatus($service_id: String!, $status: String!, $tracking_notes: String) {
    updateDeliveryStatus(service_id: $service_id, status: $status, tracking_notes: $tracking_notes) {
      service_id
      status
      tracking_notes
    }
  }
`;

// Components
const LoginForm = ({ onLogin, onToggleMode }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [login, { loading, error }] = useMutation(LOGIN_MUTATION);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const { data } = await login({ variables: { email, password } });
      localStorage.setItem('token', data.login.token);
      localStorage.setItem('user', JSON.stringify(data.login.user));
      onLogin(data.login.user);
    } catch (err) {
      console.error('Login error:', err);
    }
  };

  return (
    <div className="auth-form">
      <div className="auth-form-header">
        <h2>Welcome Back</h2>
        <p>Sign in to your account</p>
      </div>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="email">Email Address</label>
          <input
            id="email"
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            id="password"
            type="password"
            placeholder="Enter your password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit" disabled={loading} className="btn-primary">
          {loading ? 'Signing in...' : 'Sign In'}
        </button>
      </form>
      {error && <div className="error-message">Error: {error.message}</div>}
      <div className="auth-switch">
        <p>
          Don't have an account?{' '}
          <button type="button" onClick={onToggleMode} className="link-button">
            Create Account
          </button>
        </p>
      </div>
      <div className="demo-credentials">
        <h4>Demo Credentials:</h4>
        <div className="credential-item">
          <strong>Admin:</strong> admin@example.com / password
        </div>
        <div className="credential-item">
          <strong>Customer:</strong> customer1@example.com / password
        </div>
      </div>
    </div>
  );
};

const RegisterForm = ({ onLogin, onToggleMode }) => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('customer');
  const [register, { loading, error }] = useMutation(REGISTER_MUTATION);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const { data } = await register({ variables: { username, email, password, role } });
      localStorage.setItem('token', data.register.token);
      localStorage.setItem('user', JSON.stringify(data.register.user));
      onLogin(data.register.user);
    } catch (err) {
      console.error('Register error:', err);
    }
  };

  return (
    <div className="auth-form">
      <div className="auth-form-header">
        <h2>Create Account</h2>
        <p>Join our community today</p>
      </div>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="username">Username</label>
          <input
            id="username"
            type="text"
            placeholder="Choose a username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="email">Email Address</label>
          <input
            id="email"
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            id="password"
            type="password"
            placeholder="Create a password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="role">Account Type</label>
          <select id="role" value={role} onChange={(e) => setRole(e.target.value)}>
            <option value="customer">Customer</option>
            <option value="admin">Admin</option>
          </select>
        </div>
        <button type="submit" disabled={loading} className="btn-primary">
          {loading ? 'Creating Account...' : 'Create Account'}
        </button>
      </form>
      {error && <div className="error-message">Error: {error.message}</div>}
      <div className="auth-switch">
        <p>
          Already have an account?{' '}
          <button type="button" onClick={onToggleMode} className="link-button">
            Sign In
          </button>
        </p>
      </div>
    </div>
  );
};

// Toast Notification Component
const Toast = ({ message, onClose }) => {
  useEffect(() => {
    const timer = setTimeout(onClose, 3000);
    return () => clearTimeout(timer);
  }, [onClose]);
  
  return (
    <div className="toast show">
      <div className="toast-content">
        <span className="toast-icon"></span>
        <span className="toast-message">{message}</span>
      </div>
    </div>
  );
};

const ProductList = ({ user, setCurrentPage, showToast }) => {
  const [selectedCategory, setSelectedCategory] = useState(null);
  const { loading, error, data, refetch } = useQuery(GET_PRODUCTS, {
    variables: { limit: 20, offset: 0, category_id: selectedCategory }
  });
  const { data: categoriesData } = useQuery(GET_CATEGORIES);
  const [addToCart, { loading: addingToCart }] = useMutation(ADD_TO_CART, {
    refetchQueries: [{ query: GET_MY_CART }],
    onError: (error) => {
      console.error('Add to cart error:', error);
      alert('Error adding to cart: ' + error.message);
    }
  });

  const handleAddToCart = async (productId) => {
    if (!user) {
      alert('Please login to add items to cart');
      return;
    }

    console.log('Adding to cart - Product ID:', productId, 'Type:', typeof productId);
    
    try {
      // Ensure productId is converted to string for ID! type
      const result = await addToCart({ 
        variables: { 
          product_id: String(productId), // Convert to string for ID! type
          quantity: 1 
        } 
      });
      
      console.log('Add to cart result:', result);
      showToast('Product added to cart successfully!');
      
      // Short delay before navigating to cart
      setTimeout(() => setCurrentPage('cart'), 1000);
    } catch (err) {
      console.error('Error adding to cart:', err);
      console.error('Error details:', {
        message: err.message,
        graphQLErrors: err.graphQLErrors,
        networkError: err.networkError
      });
      
      // More detailed error message
      if (err.graphQLErrors && err.graphQLErrors.length > 0) {
        alert('Error adding to cart: ' + err.graphQLErrors[0].message);
      } else if (err.networkError) {
        alert('Network error: ' + err.networkError.message);
      } else {
        alert('Error adding to cart: ' + err.message);
      }
    }
  };

  if (loading) return <div className="loading-spinner"><div className="spinner"></div>Loading products...</div>;
  if (error) return <div className="error-message">Error: {error.message}</div>;

  return (
    <div className="product-section">
      <div className="section-header">
        <h2>Our Products</h2>
        <p>Discover amazing products at great prices</p>
      </div>
      
      <div className="category-filter">
        <button 
          onClick={() => setSelectedCategory(null)}
          className={`filter-btn ${selectedCategory === null ? 'active' : ''}`}
        >
          All Products
        </button>
        {categoriesData?.categories.map(category => (
          <button
            key={category.id}
            onClick={() => setSelectedCategory(category.id)}
            className={`filter-btn ${selectedCategory === category.id ? 'active' : ''}`}
          >
            {category.name}
          </button>
        ))}
      </div>
      
      <div className="products-grid">
        {data?.products.map(product => (
          <div key={product.id} className="product-card">
            <div className="product-image">
              <img 
                src={product.image_url || '/api/placeholder/300/200'} 
                alt={product.name}
                onError={(e) => {
                  e.target.src = '/api/placeholder/300/200';
                }}
              />
              {product.stock <= 5 && product.stock > 0 && (
                <div className="stock-badge low-stock">Only {product.stock} left!</div>
              )}
              {product.stock === 0 && (
                <div className="stock-badge out-of-stock">Out of Stock</div>
              )}
            </div>
            <div className="product-info">
              <div className="product-category">
                {product.category && <span className="category-tag">{product.category.name}</span>}
              </div>
              <h3 className="product-title">{product.name}</h3>
              <p className="product-description">{product.description}</p>
              <div className="product-footer">
                <div className="price-info">
                  <span className="price">${product.price}</span>
                  <span className="stock-info">Stock: {product.stock}</span>
                </div>
                {user?.role === 'customer' && (
                  <button
                    onClick={() => handleAddToCart(product.id)}
                    disabled={product.stock === 0 || addingToCart}
                    className="add-to-cart-btn"
                  >
                    {addingToCart ? (
                      <>
                        <div className="button-spinner"></div>
                        Adding...
                      </>
                    ) : product.stock === 0 ? (
                      'Out of Stock'
                    ) : (
                      'Add to Cart'
                    )}
                  </button>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
      
      {/* Add refresh button */}
      <div className="products-actions">
        <button 
          onClick={() => refetch()}
          className="btn-secondary"
          disabled={loading}
        >
           Refresh Products
        </button>
      </div>
    </div>
  );
};

// Admin components would follow similar patterns with modern styling...
const AdminPanel = ({ user }) => {
  const [showAddProduct, setShowAddProduct] = useState(false);
  const [productForm, setProductForm] = useState({
    name: '',
    description: '',
    price: '',
    category_id: '',
    stock: '',
    image_url: '',
    initialWarehouseId: '',
    initialQuantity: ''
  });
  
  const { data: categoriesData } = useQuery(GET_CATEGORIES);
  const { data: warehousesData } = useQuery(GET_WAREHOUSES);
  const [addProduct] = useMutation(ADD_PRODUCT, {
    refetchQueries: [{ query: GET_PRODUCTS }, { query: GET_INVENTORY_PRODUCTS }]
  });

  if (!user || user.role !== 'admin') {
    return <div className="auth-required">Admin access required</div>;
  }

  const handleAddProduct = async (e) => {
    e.preventDefault();
    try {
      await addProduct({
        variables: {
          ...productForm,
          price: parseFloat(productForm.price),
          category_id: parseInt(productForm.category_id),
          stock: parseInt(productForm.stock),
          initialWarehouseId: productForm.initialWarehouseId ? parseInt(productForm.initialWarehouseId) : null,
          initialQuantity: productForm.initialQuantity ? parseInt(productForm.initialQuantity) : 0
        }
      });
      alert('Product added successfully!');
      setProductForm({ name: '', description: '', price: '', category_id: '', stock: '', image_url: '', initialWarehouseId: '', initialQuantity: '' });
      setShowAddProduct(false);
    } catch (err) {
      alert('Error adding product: ' + err.message);
    }
  };

  return (
    <div className="admin-panel">
      <div className="section-header">
        <h2>Admin Panel</h2>
        <p>Manage your store products and inventory</p>
      </div>
      
      <div className="admin-actions">
        <button 
          onClick={() => setShowAddProduct(!showAddProduct)}
          className="btn-primary"
        >
          {showAddProduct ? 'Cancel' : '+ Add New Product'}
        </button>
      </div>

      {showAddProduct && (
        <div className="form-section">
          <h3>Add New Product</h3>
          <form onSubmit={handleAddProduct} className="modern-form">
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="product-name">Product Name</label>
                <input
                  id="product-name"
                  type="text"
                  placeholder="Enter product name"
                  value={productForm.name}
                  onChange={(e) => setProductForm({...productForm, name: e.target.value})}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="product-price">Price ($)</label>
                <input
                  id="product-price"
                  type="number"
                  step="0.01"
                  placeholder="0.00"
                  value={productForm.price}
                  onChange={(e) => setProductForm({...productForm, price: e.target.value})}
                  required
                />
              </div>
            </div>
            
            <div className="form-group">
              <label htmlFor="product-description">Description</label>
              <textarea
                id="product-description"
                placeholder="Describe your product"
                value={productForm.description}
                onChange={(e) => setProductForm({...productForm, description: e.target.value})}
                rows="3"
              />
            </div>
            
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="product-category">Category</label>
                <select
                  id="product-category"
                  value={productForm.category_id}
                  onChange={(e) => setProductForm({...productForm, category_id: e.target.value})}
                  required
                >
                  <option value="">Select Category</option>
                  {categoriesData?.categories.map(category => (
                    <option key={category.id} value={category.id}>{category.name}</option>
                  ))}
                </select>
              </div>
              <div className="form-group">
                <label htmlFor="product-stock">Stock Quantity</label>
                <input
                  id="product-stock"
                  type="number"
                  placeholder="0"
                  value={productForm.stock}
                  onChange={(e) => setProductForm({...productForm, stock: e.target.value})}
                  required
                />
              </div>
            </div>
            
            <div className="form-group">
              <label htmlFor="product-image">Image URL</label>
              <input
                id="product-image"
                type="url"
                placeholder="https://example.com/image.jpg"
                value={productForm.image_url}
                onChange={(e) => setProductForm({...productForm, image_url: e.target.value})}
              />
            </div>
            
            <div className="form-section-title">
              <h4>Initial Inventory (Optional)</h4>
            </div>
            
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="initial-warehouse">Warehouse</label>
                <select
                  id="initial-warehouse"
                  value={productForm.initialWarehouseId}
                  onChange={(e) => setProductForm({...productForm, initialWarehouseId: e.target.value})}
                >
                  <option value="">Select Warehouse (Optional)</option>
                  {warehousesData?.warehouses.map(warehouse => (
                    <option key={warehouse.id_warehouse} value={warehouse.id_warehouse}>
                      {warehouse.name}
                    </option>
                  ))}
                </select>
              </div>
              <div className="form-group">
                <label htmlFor="initial-quantity">Initial Quantity</label>
                <input
                  id="initial-quantity"
                  type="number"
                  placeholder="0"
                  value={productForm.initialQuantity}
                  onChange={(e) => setProductForm({...productForm, initialQuantity: e.target.value})}
                  disabled={!productForm.initialWarehouseId}
                />
              </div>
            </div>
            
            <button type="submit" className="btn-primary btn-large">
              Add Product
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

// Main App Component with modern navigation
const AppContent = () => {
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('user')));
  const [currentPage, setCurrentPage] = useState('products');
  const [showLogin, setShowLogin] = useState(true);
  const [toast, setToast] = useState('');
  const [sidebarOpen, setSidebarOpen] = useState(false);

  const showToast = (msg) => setToast(msg);

  useEffect(() => {
    if (toast) {
      const timer = setTimeout(() => setToast(''), 3000);
      return () => clearTimeout(timer);
    }
  }, [toast]);

  const handleLogin = (userData) => {
    setUser(userData);
    setCurrentPage('products');
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    setCurrentPage('products');
    setSidebarOpen(false);
  };

  const renderContent = () => {
    if (!user) {
      return showLogin ? 
        <LoginForm onLogin={handleLogin} onToggleMode={() => setShowLogin(!showLogin)} /> :
        <RegisterForm onLogin={handleLogin} onToggleMode={() => setShowLogin(!showLogin)} />;
    }

    switch (currentPage) {
      case 'products':
        return <ProductList user={user} setCurrentPage={setCurrentPage} showToast={showToast} />;
      case 'cart':
        if (user?.role !== 'customer') return <div className="auth-required">Only customers can access cart.</div>;
        return <Cart user={user} />;
      case 'orders':
        if (user?.role !== 'customer') return <div className="auth-required">Only customers can access orders.</div>;
        return <Orders user={user} />;
      case 'profile':
        return <Profile user={user} />;
      case 'admin':
        if (user?.role !== 'admin') return <div className="auth-required">Only admins can access admin panel.</div>;
        return <AdminPanel user={user} />;
      case 'all-orders':
        if (user?.role !== 'admin') return <div className="auth-required">Only admins can access all orders.</div>;
        return <AdminAllOrders user={user} />;
      case 'delivery-tracking':
        return <DeliveryTrackingPage />;
      default:
        return <ProductList user={user} setCurrentPage={setCurrentPage} showToast={showToast} />;
    }
  };

  return (
    <div className="app">
      <header className="app-header">
        <div className="header-content">
          <div className="header-left">
            <button 
              className="mobile-menu-btn"
              onClick={() => setSidebarOpen(!sidebarOpen)}
            >
              
            </button>
            <h1 className="app-title">belilokal.id</h1>
          </div>
          
          {user && (
            <nav className={`nav-menu ${sidebarOpen ? 'nav-open' : ''}`}>
              <div className="nav-overlay" onClick={() => setSidebarOpen(false)}></div>
              <div className="nav-content">
                <div className="nav-header">
                  <h3>Navigation</h3>
                  <button className="nav-close" onClick={() => setSidebarOpen(false)}></button>
                </div>
                
                <div className="nav-items">
                  <button
                    className={`nav-item ${currentPage === 'products' ? 'active' : ''}`}
                    onClick={() => { setCurrentPage('products'); setSidebarOpen(false); }}
                  >
                    <span className="nav-icon"></span>
                    Products
                  </button>
                  
                  {user.role === 'customer' && (
                    <>
                      <button
                        className={`nav-item ${currentPage === 'cart' ? 'active' : ''}`}
                        onClick={() => { setCurrentPage('cart'); setSidebarOpen(false); }}
                      >
                        <span className="nav-icon"></span>
                        Cart
                      </button>
                      <button
                        className={`nav-item ${currentPage === 'orders' ? 'active' : ''}`}
                        onClick={() => { setCurrentPage('orders'); setSidebarOpen(false); }}
                      >
                        <span className="nav-icon"></span>
                        My Orders
                      </button>
                    </>
                  )}
                  
                  {user.role === 'admin' && (
                    <>
                      <button
                        className={`nav-item ${currentPage === 'admin' ? 'active' : ''}`}
                        onClick={() => { setCurrentPage('admin'); setSidebarOpen(false); }}
                      >
                        <span className="nav-icon"></span>
                        Admin Panel
                      </button>
                      <button
                        className={`nav-item ${currentPage === 'all-orders' ? 'active' : ''}`}
                        onClick={() => { setCurrentPage('all-orders'); setSidebarOpen(false); }}
                      >
                        <span className="nav-icon"></span>
                        All Orders
                      </button>
                    </>
                  )}
                  
                  <button
                    className={`nav-item ${currentPage === 'delivery-tracking' ? 'active' : ''}`}
                    onClick={() => { setCurrentPage('delivery-tracking'); setSidebarOpen(false); }}
                  >
                    <span className="nav-icon"></span>
                    Track Delivery
                  </button>
                  
                  <button
                    className={`nav-item ${currentPage === 'profile' ? 'active' : ''}`}
                    onClick={() => { setCurrentPage('profile'); setSidebarOpen(false); }}
                  >
                    <span className="nav-icon"></span>
                    Profile
                  </button>
                </div>
                
                <div className="nav-footer">
                  <div className="user-info">
                    <span className="user-name">{user.username}</span>
                    <span className="user-role">{user.role}</span>
                  </div>
                  <button onClick={handleLogout} className="logout-btn">
                    <span className="nav-icon"></span>
                    Logout
                  </button>
                </div>
              </div>
            </nav>
          )}
        </div>
      </header>
      
      <main className="main-content">
        {renderContent()}
      </main>
      
      {toast && <Toast message={toast} onClose={() => setToast('')} />}
      
      {/* Add some additional CSS for spinner */}
      <style jsx>{`
        .button-spinner {
          width: 16px;
          height: 16px;
          border: 2px solid rgba(255, 255, 255, 0.3);
          border-top: 2px solid white;
          border-radius: 50%;
          animation: spin 1s linear infinite;
          margin-right: 0.5rem;
        }

        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }

        .products-actions {
          display: flex;
          justify-content: center;
          margin-top: 2rem;
          padding-top: 2rem;
          border-top: 2px solid #f3f4f6;
        }
      `}</style>
    </div>
  );
};

function App() {
  return (
    <ApolloProvider client={client}>
      <AppContent />
    </ApolloProvider>
  );
}

export default App;
